import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem('tasks');
    return saved ? JSON.parse(saved) : [];
  });
  const [filter, setFilter] = useState('all');
  const [sortOrder, setSortOrder] = useState('newest');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    const trimmed = task.trim();
    if (!trimmed) return alert('Task cannot be empty!');
    const newTask = {
      id: Date.now(),
      text: trimmed,
      completed: false,
    };
    setTasks([newTask, ...tasks]);
    setTask('');
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const toggleComplete = (id) => {
    setTasks(tasks.map(task =>
      task.id === id ? { ...task, completed: !task.completed } : task
    ));
  };

  const startEditing = (id, text) => {
    setEditId(id);
    setEditText(text);
  };

  const saveEdit = (id) => {
    setTasks(tasks.map(task =>
      task.id === id ? { ...task, text: editText } : task
    ));
    setEditId(null);
    setEditText('');
  };

  const getFilteredTasks = () => {
    let filtered = [...tasks];
    if (filter === 'completed') filtered = filtered.filter(t => t.completed);
    if (filter === 'pending') filtered = filtered.filter(t => !t.completed);
    if (sortOrder === 'oldest') filtered.reverse();
    return filtered;
  };

  return (
    <div className="todo-container">
      <h2>To-Do List</h2>
      <div className="input-group">
        <input
          value={task}
          onChange={e => setTask(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && addTask()}
          placeholder="Enter a new task"
        />
        <button onClick={addTask}>Add</button>
      </div>

      <div className="controls">
        <select value={filter} onChange={e => setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="pending">Pending</option>
        </select>

        <select value={sortOrder} onChange={e => setSortOrder(e.target.value)}>
          <option value="newest">Newest First</option>
          <option value="oldest">Oldest First</option>
        </select>
      </div>

      <ul className="task-list">
        {getFilteredTasks().map(t => (
          <li
            key={t.id}
            className={`task-box ${t.completed ? 'completed-task' : 'incomplete-task'}`}
          >
            <label className="task-left">
              <input
                type="checkbox"
                checked={t.completed}
                onChange={() => toggleComplete(t.id)}
              />
              {editId === t.id ? (
                <input
                  className="edit-input"
                  value={editText}
                  onChange={e => setEditText(e.target.value)}
                  onBlur={() => saveEdit(t.id)}
                  onKeyDown={e => e.key === 'Enter' && saveEdit(t.id)}
                  autoFocus
                />
              ) : (
                <span>{t.text}</span>
              )}
            </label>
            <div className="task-actions">
              <button onClick={() => startEditing(t.id, t.text)}>üñâ</button>
              <button onClick={() => deleteTask(t.id)}>üóëÔ∏è</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
